import './App.css';
import io from "socket.io-client"
import axios from "axios"
import {useState} from "react"

// conexion para escuchar eventos 

const socket= io("http://localhost:4000")


function App() {

  const [nickname, setNickname] = useState('')
  const [disabled, setDisabled] = useState(false)

  const [message, setMessage] = useState('')
  const [messages, setMessages] = useState([])
  const [storedMessages, setStoredMessages] = useState([])
  const [firstTime, setfirstTime] = useState(false)

  useEffect(() =>{
    const receivedMessage = (message) =>{
      //console.log(message)
      setMessages([message, ...messages])

    }
    socket.on('message', receivedMessage)

    //Desuscribimos el estado del componente cuando ya no es necesario utilizarlo
    return () => {
      socket.off('message', receivedMessage)
    }


  }, [messages])

  //Cargamos los mensajes guardados en la BDD la primera vez
  if(!firstTime){
    axios.get(url + "messages").then(res => {
      setStoredMessages(res.data.messages);
    })
    setfirstTime(true)
  }


  const handlerSubmit = (e) => {
    //Evitamos recargar la página
    e.preventDefault()

    //Enviamos el mensaje sólo si se ha establecido un nickname
    if(nickname !== ''){
      //console.log(message)
      //Enviamos el mensaje al servidor
      socket.emit('message', message, nickname)

      //Nuestro mensaje
      const newMessage = {
        body: message,
        from: 'Yo'
      }
      //Añadimos el mensaje y el resto de mensajes enviados
      setMessages([newMessage, ...messages])
      //Limpiamos el mensaje
      setMessage('')

      //Petición http por POST para guardar el artículo:
      axios.post(url + 'save', {
        message: message,
        from: nickname
      })

    }else{
      alert('Para enviar mensajes debes establecer un nickname!!!')
    }

  }

  const nicknameSubmit = (e) => {
    e.preventDefault()
    setNickname(nickname)
    //console.log(nickname)
    setDisabled(true)
  }

  return (
    <div className="App">
      <div className="container mt-3">
        <div className=" card">
          <div className=" card-body">
            <h5 className=" text-container">Chat</h5>

            {/*NickName*/}

            <form onSubmit={nicknameSubmit}>
              <div className="d-flex mb-3">
                <input type="text" className="form-control" id="nickname" placeholder="Nickname..." onChange={e => setNickname(e.target.value)} value={nickname} required />
                <button className="btn btn-success mx-3" type="submit" id="btn-nickname" >Establecer</button>
              </div>
            </form>

            {/* chat form */}

            <form onSubmit={handlerSubmit} >
              <div className="d-flex">
                <input type="text" className="form-control" placeholder="Mensaje..." onChange={e => setMessage(e.target.value)} value={message} />
                <button className="btn btn-success mx-3" type="submit">Enviar</button>
              </div>
            </form>

          </div>
        </div>

           {/* chat messages */}

           <div className=" card mt-3 mb-3 shadow border-0" id="content-cha">
              <div className="card-body">


              </div>
            </div>

      </div>
    </div>
  );
}

export default App;
